query:
query promotionProductsItems($storeId: String, $zipCode: String, $isBrandPricingPolicyCompliant: Boolean, $skipInstallServices: Boolean = true, $skipKPF: Boolean = false, $skipSpecificationGroup: Boolean = false, $pageSize: Int, $navParam: String, $itemIds: [String], $storefilter: StoreFilter) {
  searchModel(
    navParam: $navParam
    storeId: $storeId
    itemIds: $itemIds
    storefilter: $storefilter
    isBrandPricingPolicyCompliant: $isBrandPricingPolicyCompliant
  ) {
    products(pageSize: $pageSize) {
      itemId
      dataSources
      fulfillment(storeId: $storeId, zipCode: $zipCode) {
        fulfillmentOptions {
          fulfillable
          type
          services {
            type
            locations {
              isAnchor
              locationId
              inventory {
                isOutOfStock
                quantity
                isInStock
                isLimitedQuantity
                isUnavailable
                maxAllowedBopisQty
                minAllowedBopisQty
                __typename
              }
              curbsidePickupFlag
              isBuyInStoreCheckNearBy
              distance
              state
              storeName
              storePhone
              type
              __typename
            }
            deliveryTimeline
            deliveryDates {
              startDate
              endDate
              __typename
            }
            deliveryCharge
            dynamicEta {
              hours
              minutes
              __typename
            }
            hasFreeShipping
            freeDeliveryThreshold
            totalCharge
            deliveryMessage
            earliestDeliveryDate
            __typename
          }
          __typename
        }
        anchorStoreStatus
        anchorStoreStatusType
        backordered
        backorderedShipDate
        bossExcludedShipStates
        excludedShipStates
        seasonStatusEligible
        onlineStoreStatus
        onlineStoreStatusType
        fulfillmentBundleMessage
        __typename
      }
      identifiers {
        brandName
        productType
        productLabel
        canonicalUrl
        itemId
        specialOrderSku
        storeSkuNumber
        __typename
      }
      media {
        images {
          url
          sizes
          type
          subType
          __typename
        }
        __typename
      }
      pricing(
        storeId: $storeId
        isBrandPricingPolicyCompliant: $isBrandPricingPolicyCompliant
      ) {
        value
        promotion {
          promotionTag
          type
          description {
            shortDesc
            longDesc
            __typename
          }
          dollarOff
          percentageOff
          savingsCenter
          savingsCenterPromos
          specialBuySavings
          specialBuyDollarOff
          specialBuyPercentageOff
          __typename
        }
        alternatePriceDisplay
        alternate {
          bulk {
            pricePerUnit
            thresholdQuantity
            value
            __typename
          }
          unit {
            caseUnitOfMeasure
            unitsOriginalPrice
            unitsPerCase
            value
            __typename
          }
          __typename
        }
        original
        mapAboveOriginalPrice
        mapDetail {
          percentageOff
          dollarOff
          mapPolicy
          mapOriginalPriceViolation
          mapSpecialPriceViolation
          __typename
        }
        message
        preferredPriceFlag
        specialBuy
        unitOfMeasure
        __typename
      }
      details {
        description
        descriptiveAttributes {
          value
          __typename
        }
        collection {
          name
          url
          __typename
        }
        installation {
          serviceType
          __typename
        }
        __typename
      }
      reviews {
        ratingsReviews {
          averageRating
          totalReviews
          __typename
        }
        __typename
      }
      info {
        quantityLimit
        hidePrice
        ecoRebate
        categoryHierarchy
        sskMin
        sskMax
        unitOfMeasureCoverage
        wasMaxPriceRange
        wasMinPriceRange
        productSubType {
          name
          link
          __typename
        }
        isSponsored
        sponsoredMetadata {
          campaignId
          placementId
          slotId
          sponsoredId
          trackSource
          __typename
        }
        globalCustomConfigurator {
          customExperience
          __typename
        }
        augmentedReality
        sponsoredBeacon {
          onClickBeacon
          onViewBeacon
          onClickBeacons
          onViewBeacons
          __typename
        }
        customerSignal {
          previouslyPurchased
          __typename
        }
        isBuryProduct
        isGenericProduct
        returnable
        classNumber
        hasSubscription
        isLiveGoodsProduct
        productDepartment
        __typename
      }
      taxonomy {
        breadCrumbs {
          label
          __typename
        }
        __typename
      }
      dataSource
      badges(storeId: $storeId) {
        name
        __typename
      }
      installServices(storeId: $storeId, zipCode: $zipCode) @skip(if: $skipInstallServices) {
        scheduleAMeasure
        gccCarpetDesignAndOrderEligible
        __typename
      }
      keyProductFeatures @skip(if: $skipKPF) {
        keyProductFeaturesItems {
          features {
            name
            refinementId
            refinementUrl
            value
            __typename
          }
          __typename
        }
        __typename
      }
      specificationGroup @skip(if: $skipSpecificationGroup) {
        specifications {
          specName
          specValue
          __typename
        }
        specTitle
        __typename
      }
      bundleFlag
      availabilityType {
        buyable
        discontinued
        status
        type
        __typename
      }
      __typename
    }
    __typename
  }
}
